import React, { Component } from 'react';
import Typography from '@material-ui/core/Typography';
import MaterialTable from 'material-table';
import { CloudUpload, Cancel, Star } from '@material-ui/icons/';
import swal from 'sweetalert';
import axios from 'axios';

import { forwardRef } from 'react';
import AddBox from '@material-ui/icons/AddBox';
import ArrowUpward from '@material-ui/icons/ArrowUpward';
import Check from '@material-ui/icons/Check';
import ChevronLeft from '@material-ui/icons/ChevronLeft';
import ChevronRight from '@material-ui/icons/ChevronRight';
import Clear from '@material-ui/icons/Clear';
import DeleteOutline from '@material-ui/icons/DeleteOutline';
import Edit from '@material-ui/icons/Edit';
import FilterList from '@material-ui/icons/FilterList';
import FirstPage from '@material-ui/icons/FirstPage';
import LastPage from '@material-ui/icons/LastPage';
import Remove from '@material-ui/icons/Remove';
import SaveAlt from '@material-ui/icons/SaveAlt';
import Search from '@material-ui/icons/Search';
import ViewColumn from '@material-ui/icons/ViewColumn';
import ReactLoading from "react-loading";

const tableIcons = {
    Add: forwardRef((props, ref) => <AddBox {...props} ref={ref} />),
    Check: forwardRef((props, ref) => <Check {...props} ref={ref} />),
    Clear: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Delete: forwardRef((props, ref) => <DeleteOutline {...props} ref={ref} />),
    DetailPanel: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    Edit: forwardRef((props, ref) => <Edit {...props} ref={ref} />),
    Export: forwardRef((props, ref) => <SaveAlt {...props} ref={ref} />),
    Filter: forwardRef((props, ref) => <FilterList {...props} ref={ref} />),
    FirstPage: forwardRef((props, ref) => <FirstPage {...props} ref={ref} />),
    LastPage: forwardRef((props, ref) => <LastPage {...props} ref={ref} />),
    NextPage: forwardRef((props, ref) => <ChevronRight {...props} ref={ref} />),
    PreviousPage: forwardRef((props, ref) => <ChevronLeft {...props} ref={ref} />),
    ResetSearch: forwardRef((props, ref) => <Clear {...props} ref={ref} />),
    Search: forwardRef((props, ref) => <Search {...props} ref={ref} />),
    SortArrow: forwardRef((props, ref) => <ArrowUpward {...props} ref={ref} />),
    ThirdStateCheck: forwardRef((props, ref) => <Remove {...props} ref={ref} />),
    ViewColumn: forwardRef((props, ref) => <ViewColumn {...props} ref={ref} />)
};

class SapReady extends Component {

    constructor(props) {
        super(props);
        this.state = {
            columns: [
                { title: 'Fecha Creaci贸n', field: 'creado' },
                { title: 'Cliente', field: 'CodigoCliente' },
                { title: 'Fecha de Carga', field: 'FechaCarga' },
                { title: 'Hora de Carga', field: 'HoraCarga' },
                { title: 'Tipo Pago', field: 'tipo_pago' },
                { title: 'C贸digo Cliente SAP', field: 'CodigoClienteSap', render: rowData => <div className={rowData.CodigoClienteSapExist > 0 ? "" : "no_margin"} dangerouslySetInnerHTML={{ __html: rowData.CodigoClienteSap }} /> },
                { title: 'C贸digo Transporte SAP', field: 'codigoTransporte', render: rowData => <div className={rowData.codigoTransporteExist > 0 ? "" : "no_margin"} dangerouslySetInnerHTML={{ __html: rowData.codigoTransporte }} /> },
                { title: 'C贸digo Proveedor SAP', field: 'CodigoProveedorSAP', render: rowData => <div className={rowData.CodigoProveedorSAPExist > 0 ? "" : "no_margin"} dangerouslySetInnerHTML={{ __html: rowData.CodigoProveedorSAP }} /> },
            ],
            uploading: false
        };
    }

    render() {

        let orders = null;

        if (this.props.orders) orders = this.props.orders;

        // Show only the anuladas orders
        if (orders)
            orders = orders.filter(
                (key) =>
                    key.CreditoValidado !== null && key.HorarioAsignado !== null && this.props.prices_flag && key.sid !== "5" && key.sid !== "6" && key.Programado !== null
            );


        return (
            <div className="main-container">
                <Typography variant="h3" component="h1" gutterBottom>
                    Pedidos Listos para subir a SAP
                </Typography>
                {
                    this.state.uploading ? (
                        <div className="loading">
                            <ReactLoading type="cubes" color="#F33E36" />
                        </div>
                    ) : ""
                }
                <div className="landing-container with-spacing">
                    {
                        orders ? (
                            <MaterialTable
                                icons={tableIcons}
                                columns={this.state.columns}
                                data={orders}
                                title="Listado de Pedidos"
                                options={{
                                    pageSize: 20
                                }}
                                actions={[
                                    rowData => ({
                                        icon: () => <Star color="secondary" />,
                                        tooltip: 'VIP',
                                        hidden: rowData.star === 'N'
                                    }),
                                    rowData => ({
                                        icon: CloudUpload,
                                        tooltip: 'Subir a SAP',
                                        hidden: rowData.CodigoClienteSap === '' || rowData.CodigoClienteSap === null || rowData.codigoTransporte === '' || rowData.codigoTransporte === null || rowData.CodigoProveedorSAP === '' || rowData.CodigoProveedorSAP === null || parseInt(rowData.oid) === 6 || rowData.CodigoClienteSapExist === 0 || rowData.codigoTransporteExist === 0 || rowData.CodigoProveedorSAPExist === 0,
                                        onClick: (event, rowData) => {
                                            if (!this.state.uploading)
                                                swal("驴Subir Orden a SAP?", "Comentario", {
                                                    buttons: ["No", "Si"],
                                                    icon: "warning",
                                                    content: "input",
                                                }).then((subir) => {
                                                    if (subir !== null) {
                                                        this.setState({ uploading: true });
                                                        let t_ = this;
                                                        axios.post(this.props.url + "api/push-order-sap", {
                                                            id: rowData.id,
                                                            user: window.localStorage.getItem('tp_uid'),
                                                            comentario: subir
                                                        })
                                                            .then(function (response) {
                                                                if (response.data[0]) {
                                                                    t_.props.load_orders();
                                                                    swal("Exito!", "Se subi贸 la orden a SAP.", {
                                                                        icon: "success"
                                                                    });
                                                                } else {
                                                                    // swal("Error", "Contactar al Administrador", {
                                                                    swal("Error", response.data[1], {
                                                                        icon: "error",
                                                                        html: true
                                                                    });
                                                                }
                                                                t_.setState({ uploading: false });
                                                            })
                                                            .catch(function (error) {
                                                                console.log(error);
                                                            });
                                                    }
                                                    // console.log(rowData);
                                                });
                                            // this.props.history.push("/creditos/" + rowData.id);
                                        }
                                    }),
                                    rowData => ({
                                        icon: Cancel,
                                        tooltip: 'Anular Orden',
                                        onClick: (event, rowData) => {
                                            // this.props.history.push("/creditos/" + rowData.id);
                                            swal("Anular Orden?", "Comentario", {
                                                buttons: ["No", "Si"],
                                                icon: "warning",
                                                content: "input",
                                            }).then((anular) => {
                                                if (anular !== null) {
                                                    let t_ = this;
                                                    axios.post(this.props.url + "api/cancel-order", {
                                                        id: rowData.id,
                                                        user: window.localStorage.getItem('tp_uid'),
                                                        comentario: anular
                                                    })
                                                        .then(function () {
                                                            // t.setState({ clientes: response.data });
                                                            t_.props.load_orders();
                                                        })
                                                        .catch(function (error) {
                                                            console.log(error);
                                                        });
                                                }
                                            });
                                        }
                                    })

                                ]}
                                localization={{
                                    pagination: {
                                        labelDisplayedRows: '{from}-{to} de {count}',
                                        labelRowsSelect: 'Filas'
                                    },
                                    toolbar: {
                                        nRowsSelected: '{0} filas(s) seleccionadas',
                                        searchPlaceholder: 'Buscar'
                                    },
                                    header: {
                                        actions: 'Acciones'
                                    },
                                    body: {
                                        emptyDataSourceMessage: 'No existen ordenes',
                                        filterRow: {
                                            filterTooltip: 'Filter'
                                        }
                                    }

                                }}
                            />
                        ) : ""
                    }
                </div>
            </div>
        );
    }
}
export default SapReady;
